<?phpnamespace app\api\controller\v1;use app\api\service\UserToken;use app\api\validate\TokenGet;/* * 获取令牌登录 */class Token{// 用户获取令牌 post安全性增强    public function getToken($code='',$name='')    {               (new TokenGet())->goCheck();        $wx = new UserToken($code,$name);        $token = $wx->get();        return [            'token' => $token        ];    }}