<?phpnamespace app\api\controller\v1;use app\lib\exception\ParameterException;use think\Controller;use think\validate;// 文件上传处理class Upload extends Controller{    public function index()    {        return $this->fetch();    }// 证件上传提交    public function upload()    {        $file = $this->request->file('file');        $result = [];        if ($file) {            $info = $file->validate(['size' => 1024 * 1024 * 4, 'ext' => 'png,jpg'])->move(ROOT_PATH . 'public' . DS . 'uploads');//                ->move(ROOT_PATH . 'public' . DS . 'upload' . DS . date("Ymd", time()), time());            if ($info) {                $result = [                    'code' => 200,                    'msg' => 'upload success',                    'filename' => '/uploads' . '/' . str_replace('\\', '/', $info->getSaveName())                ];            }        } else {            $result = [                'code' => -200,                'msg' => 'upload error'            ];        }        return json($result);    }}