<?php/** * Created by PhpStorm. * User: THINK * Date: 2018/4/4 * Time: 15:46 */namespace app\api\model;use think\Model;// 评论表class ShopEval extends Model{    //时间自增   // protected $updateTime = 'time';    protected $createTime = 'time';    protected $autoWriteTimestamp = true;    //过滤不存在的键值    protected $field = true;    //判断是否评论过    public static function CheckComment($order_id){        $comment = self::where('order_id','=',$order_id)                   //->where('user_id',$user_id)                   ->find();        return  $comment;    }    //获取我的评论    public static function getUserComment($order_id){        $comment = self::where('order_id','=',$order_id)            ->find();        return  $comment;    }    //获取某商家评论    public static function getShopComment($shop_id){        $comment = self::where('shop_id','=',$shop_id)            ->select();        return  $comment;    }}