<?phpnamespace app\api\service;use app\api\model\User;use app\lib\exception\OrderException;class DeliveryMessage extends WxMessage{    const DELIVERY_MSG_ID = 's8SRqgUFlBnXznl0wuvkRwn1CyxXQG8-v0cyq92o8Sc';// 小程序模板消息ID号    //    private $productName;    //    private $devliveryTime;    //    private $order    public function sendDeliveryMessage( $prepay_id,$openid='',$order_id='',$cost='',$msg='',$tplJumpPage = '')    {//        if (!$order) {////            throw new OrderException();////        }        $this->tplID = self::DELIVERY_MSG_ID;        $this->formID = $prepay_id; //$order->prepay_id;        $this->page = $tplJumpPage;        $this->prepareMessageData($order_id,$cost,$msg);        $this->emphasisKeyWord='keyword2.DATA';//        return parent::sendMessage($this->getUserOpenID($order->user_id));        return parent::sendMessage($openid);    }    private function prepareMessageData($order_id,$cost,$msg)    {        $dt = new \DateTime();        $data = [            'keyword1' => [                'value' =>$order_id,            ],            'keyword2' => [                'value' => $cost,                'color' => '#27408B'            ],		   'keyword3' => [                'value' =>$msg,            ],            'keyword4' => [                'value' => $dt->format("Y-m-d H:i")            ]        ];        $this->data = $data;    }    private function getUserOpenID($uid)    {        $user = User::get($uid);        if (!$user) {            throw new UserException();        }        return $user->user_wxid;    }}