<?php// 腾讯地图class Tmap{    /*     * 根据地址赖获取经纬度     * @param $address     */    public static function getLngLat($address){        if(!$address){            return '';        }        $data=[            'address'=>$address,            'key'=>config('tmap.key'),            'output'=>'json',        ];        $url=config('tmap.tengxu_map_url').config('tmap.address').'?'.http_build_query($data);        // 1 file_get_contents($data)        // 2 curl        $result=doCurl($url,0,$data);        if($result){            return json_decode($result,true);        }else{            return [];        }    }}